import React, { useState } from 'react';
import { Input, Button, List, Image, Typography } from 'antd';
import { SendOutlined } from '@ant-design/icons';
import { MidjourneyAPi } from '../request';
import { MJMessage } from 'midjourney';
import { Message } from '../interfaces/message';

const { TextArea } = Input;
const { Text } = Typography

const Index: React.FC = () => {
  const [inputValue, setInputValue] = useState('');
  const [inputDisable, setInputDisable] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);

  const handleMessageSend = async () => {
    let newMessage: Message = {
      text: inputValue.trim(),
      img: "data:image/png;base64,"
    };

    if (newMessage.text) {
      const oldMessages = messages;
      setInputDisable(true);
      setMessages([...oldMessages, newMessage]);
      console.log("newMessage.text")
      await MidjourneyAPi(JSON.stringify({ prompt: newMessage.text }), (data: MJMessage) => {
        console.log(data)
        newMessage.img = data.uri;
        setMessages([...oldMessages, newMessage]);
      })
      setInputValue('');
      setInputDisable(false);
    }
  };

  const renderMessage = ({ text, img }: Message) => {
    // const html = marked(text);
    return (
      <List.Item className='flex flex-col space-y-4 justify-start items-start' style={{
        alignItems: 'flex-start',
      }}>
        <Text >{text}</Text>
        <Image className='ml-2 rounded-xl' width={400} src={img}
        />
      </List.Item>
    );
  };

  return (
    <div className='w-full mx-auto px-4 h-full overflow-y-hidden' >
      <List
        className=' mx-auto xl:w-3/5 w-4/5 justify-start overflow-y-auto'
        style={{
          height: 'calc(100vh - 96px)',
        }}
        dataSource={messages}
        renderItem={renderMessage}
      />
      <div className='absolute z-10 w-2/4 xl:w-2/5 right-0 bottom-10 left-0 mx-auto '>
        <TextArea
          className='w-full'
          disabled={inputDisable}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter' && e.shiftKey) {
              setInputValue(`${inputValue}\n`);
              e.preventDefault();
            } else if (e.key === 'Enter') {
              handleMessageSend();
              e.preventDefault();
            }
          }}
          placeholder="Start typing your main idea..."
          autoSize={{ minRows: 1, maxRows: 6 }}
          style={{ paddingRight: 30 }}
        />
        <Button
          className='absolute'
          type="primary"
          onClick={handleMessageSend}
          loading={inputDisable}
          icon={<SendOutlined style={{ color: "#000" }} />}
          title='Send'
          style={{ position: 'absolute', bottom: 0, right: 0, background: 'transparent', border: 'none', boxShadow: 'none' }}
        />
      </div>
    </div>
  );
};

export default Index;
