import React, { useState } from "react";
import { Input, Button, List, Image, Typography } from "antd";
import { SendOutlined } from "@ant-design/icons";
import { Imagine, Upscale, Variation } from "../request";
import { MJMessage } from "midjourney";
import { Message } from "../interfaces/message";
import Tag from "../components/tag";

const { TextArea } = Input;
const { Text } = Typography;

const Index: React.FC = () => {
  const [inputValue, setInputValue] = useState("");
  const [inputDisable, setInputDisable] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);

  const handleMessageSend = async () => {
    let newMessage: Message = {
      text: inputValue.trim(),
      hasTag: false,
      progress: "waiting start",
      img: "data:image/png;base64,",
    };

    if (newMessage.text) {
      const oldMessages = messages;
      setInputDisable(true);
      setMessages([...oldMessages, newMessage]);
      await Imagine(
        JSON.stringify({ prompt: newMessage.text }),
        (data: MJMessage) => {
          console.log(data);
          newMessage.img = data.uri;
          if (data.id) {
            newMessage.hasTag = true;
          }
          newMessage.msgHash = data.hash;
          newMessage.msgID = data.id;
          newMessage.progress = data.progress;
          newMessage.content = data.content;
          setMessages([...oldMessages, newMessage]);
        }
      );
      setInputValue("");
      setInputDisable(false);
    }
  };

  const upscale = async (
    pormpt: string,
    msgId: string,
    msgHash: string,
    index: number
  ) => {
    let newMessage: Message = {
      text: `${pormpt} upscale U${index}`,
      hasTag: false,
      progress: "waiting start",
      img: "data:image/png;base64,",
    };

    const oldMessages = messages;
    setInputDisable(true);
    setMessages([...oldMessages, newMessage]);
    await Upscale(
      JSON.stringify({ content: pormpt, index, msgId, msgHash }),
      (data: MJMessage) => {
        newMessage.img = data.uri;
        newMessage.msgHash = data.hash;
        newMessage.msgID = data.id;
        newMessage.content = data.content;
        newMessage.progress = data.progress;
        setMessages([...oldMessages, newMessage]);
      }
    );
    setInputDisable(false);
  };
  const variation = async (
    content: string,
    msgId: string,
    msgHash: string,
    index: number
  ) => {
    let newMessage: Message = {
      text: `${content} variation V${index}`,
      hasTag: false,
      progress: "waiting start",
      img: "data:image/png;base64,",
    };

    const oldMessages = messages;
    setInputDisable(true);
    setMessages([...oldMessages, newMessage]);
    await Variation(
      JSON.stringify({ content, index, msgId, msgHash }),
      (data: MJMessage) => {
        newMessage.img = data.uri;
        if (data.uri.endsWith(".png")) {
          newMessage.hasTag = true;
        }
        newMessage.msgHash = data.hash;
        newMessage.msgID = data.id;
        newMessage.content = data.content;
        newMessage.progress = data.progress;
        setMessages([...oldMessages, newMessage]);
      }
    );
    setInputDisable(false);
  };
  const tagClick = (
    content: string,
    msgId: string,
    msgHash: string,
    tag: string
  ) => {
    switch (tag) {
      case "V1":
        variation(content, msgId, msgHash, 1);
        break;
      case "V2":
        variation(content, msgId, msgHash, 2);
        break;
      case "V3":
        variation(content, msgId, msgHash, 3);
        break;
      case "V4":
        variation(content, msgId, msgHash, 4);
        break;
      case "U1":
        upscale(content, msgId, msgHash, 1);
        break;
      case "U2":
        upscale(content, msgId, msgHash, 2);
        break;
      case "U3":
        upscale(content, msgId, msgHash, 3);
        break;
      case "U4":
        upscale(content, msgId, msgHash, 4);
        break;
      default:
        break;
    }
  };
  const renderMessage = ({
    text,
    img,
    hasTag,
    msgHash,
    msgID,
    progress,
    content,
  }: Message) => {
    if (process.env.NEXT_PUBLIC_IMAGE_PREFIX) {
      img = img.replace(
        "https://cdn.discordapp.com/",
        process.env.NEXT_PUBLIC_IMAGE_PREFIX
      );
    }
    return (
      <List.Item
        className="flex flex-col space-y-4 justify-start items-start"
        style={{
          alignItems: "flex-start",
        }}
      >
        <Text>
          {text} {`(${progress})`}
        </Text>

        <Image className="ml-2 rounded-xl" width={400} src={img} />

        {hasTag && (
          <Tag
            Data={["V1", "V2", "V3", "V4"]}
            onClick={(tag) =>
              tagClick(String(content), String(msgID), String(msgHash), tag)
            }
          />
        )}
        {hasTag && (
          <Tag
            Data={["U1", "U2", "U3", "U4"]}
            onClick={(tag) =>
              tagClick(String(content), String(msgID), String(msgHash), tag)
            }
          />
        )}
      </List.Item>
    );
  };

  return (
    <div className="w-full mx-auto px-4 h-full overflow-y-hidden">
      <List
        className=" mx-auto xl:w-3/5 w-4/5 justify-start overflow-y-auto"
        style={{
          height: "calc(100vh - 96px)",
        }}
        dataSource={messages}
        renderItem={renderMessage}
      />
      <div className="absolute z-10 w-3/4 xl:w-3/5 right-0 bottom-10 left-0 mx-auto ">
        <TextArea
          className="w-full"
          disabled={inputDisable}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter" && e.shiftKey) {
              setInputValue(`${inputValue}\n`);
              e.preventDefault();
            } else if (e.key === "Enter") {
              handleMessageSend();
              e.preventDefault();
            }
          }}
          placeholder="Start typing your main idea..."
          autoSize={{ minRows: 1, maxRows: 6 }}
          style={{ paddingRight: 30 }}
        />
        <Button
          className="absolute"
          type="primary"
          onClick={handleMessageSend}
          loading={inputDisable}
          icon={<SendOutlined style={{ color: "#000" }} />}
          title="Send"
          style={{
            position: "absolute",
            bottom: 0,
            right: 0,
            background: "transparent",
            border: "none",
            boxShadow: "none",
          }}
        />
      </div>
    </div>
  );
};

export default Index;
